pragma solidity ^0.4.11;
import './LearnerLearningProvider.sol';

contract ProviderIndex {
    struct LearningRecordDescriptor {
        address llpcAddress;
        bytes recordType;
    }

    mapping(address => LearningRecordDescriptor[]) private learners2LearningRecords;
    mapping(address => bool) public duplicateTracker;
    mapping(address => bool) public duplicateLearnerTracker;
    address[] private myLearners;
    address public owner;

    function ProviderIndex(address _owner) public {
        owner = _owner;
    }

    function insertLearningRecord(address learner, address llpcContract, bytes recordType) public payable {
        LearnerLearningProvider llpc = LearnerLearningProvider(llpcContract);
        if ( duplicateTracker[llpcContract] == false && llpc.canWrite(msg.sender) == true) {
            LearningRecordDescriptor memory ld = LearningRecordDescriptor(llpcContract, recordType);
            learners2LearningRecords[learner].push(ld);
            duplicateTracker[llpcContract] = true;
        }
    }

    function insertLearner(address learner) public payable {
            if (duplicateLearnerTracker[learner] == false) {
                myLearners.push(learner);
                //duplicateLearnerTracker[learner] = true;
            }
    }

    function getOwner() public view returns (address _owner) {
        return owner;
    }

    function getLearners() public view returns (address[]) {
        return myLearners;
    }

    function getLearningRecordsByLearner(address learner, uint start, uint end) public view returns (address[]) {
        address[] memory res = new address[](end-start);
        for (var index = start; index<learners2LearningRecords[learner].length && index <= end; index++) {
            res[(start-index)] = learners2LearningRecords[learner][index].llpcAddress;
        }
        return res;
    }

    function getLearningRecordsByRecordType(address learner, uint start, uint end, bytes recordType) public view returns (address[]) {
        address[] memory res = new address[](end-start);
        for (var index = start; index<learners2LearningRecords[learner].length && index<=end; index++) {
            if (keccak256(learners2LearningRecords[learner][index].recordType) == keccak256(recordType)) {
                res[(start-index)] = learners2LearningRecords[learner][index].llpcAddress;
            }
        }
        return res;
    }

    function getLearningRecordsByRecordTypeAndLearners(address[] learners, uint start, uint end, bytes recordType) public view returns (address[]) {
        address[] memory res = new address[](end-start);
        for (var index2 = start; index2 <= learners.length; index2++) {
            for (var index = start; index < learners2LearningRecords[learners[index2]].length && index <= end; index++) {
            if (keccak256(learners2LearningRecords[learners[index2]][index].recordType) == keccak256(recordType)) {
                res[(start-index)] = learners2LearningRecords[learners[index2]][index].llpcAddress;
            }
        }
        }
        return res;
    }

}
