pragma solidity ^0.4.0;
import './LearnerLearningProvider.sol';

contract ProviderIndex {
    struct LearningRecordDescriptor {
        address llpcAddress;
        string recordType;
    }

    mapping(address => LearningRecordDescriptor[]) learners2LearningRecords;
    mapping(address => bool) duplicateTracker;
    mapping(address => bool) duplicateLearnerTracker;
    address[] myLearners;
    address _owner;

    function ProviderIndex(address owner) public {
        _owner = owner;
    }

    function insertLearningRecord(address learner, address llpcContract, string recordType) public payable {
        LearnerLearningProvider llpc = LearnerLearningProvider(llpcContract);
        if (llpc.getOwner() == _owner && !duplicateTracker[llpcContract] && llpc.getProvider() == learner) {
            learners2LearningRecords[learner].push(LearningRecordDescriptor(llpcContract, recordType));
            duplicateTracker[llpcContract] = true;
            if (!duplicateLearnerTracker[learner]) {
                myLearners.push(learner);
                duplicateLearnerTracker[learner] = true;
            }
        }
    }

    function getLearners() public view returns (address[]) {
        return myLearners;
    }

    function getLearningRecordsByLearner(address learner, uint start, uint end) public view returns (address[]) {
        address[] memory res = new address[](end-start);
        for (var index = start; index<learners2LearningRecords[learner].length && index <= end; index++) {
            res[(start-index)] = learners2LearningRecords[learner][index].llpcAddress;
        }
        return res;
    }

    function getLearningRecordsByRecordType(address learner, uint start, uint end, string recordType) public view returns (address[]) {
        address[] memory res = new address[](end-start);
        for (var index = start; index<learners2LearningRecords[learner].length && index<=end; index++) {
            if (keccak256(learners2LearningRecords[learner][index].recordType) == keccak256(recordType)) {
                res[(start-index)] = learners2LearningRecords[learner][index].llpcAddress;
            }
        }
        return res;
    }

    function getLearningRecordsByRecordTypeAndLearners(address[] learners, uint start, uint end, string recordType) public view returns (address[]) {
        address[] memory res = new address[](end-start);
        for (var index2 = start; index2 <= learners.length; index2++) {
            for (var index = start; index < learners2LearningRecords[learners[index2]].length && index <= end; index++) {
            if (keccak256(learners2LearningRecords[learners[index2]][index].recordType) == keccak256(recordType)) {
                res[(start-index)] = learners2LearningRecords[learners[index2]][index].llpcAddress;
            }
        }
        }
        return res;
    }

}
